  .font-serif {
    font-family: 'Cormorant Garamond', serif;
  }

  .font-playfair {
    font-family: 'Playfair Display', serif;
  }

  /* Text shadow utilities for better visibility */
  .text-shadow-lg {
    text-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
  }

  .text-shadow-md {
    text-shadow: 0 2px 4px rgba(0, 0, 0, 0.2);
  }

  .font-poppins {
    font-family: 'Poppins', sans-serif;
  }

  .font-crimson {
    font-family: 'Crimson Text', serif;
  }

  .font-lora {
    font-family: 'Lora', serif;
  }

  .font-inter {
    font-family: 'Inter', sans-serif;
  }

  /* Indian-inspired decorative elements */
  .mandala-border {
    border-image: radial-gradient(circle, hsl(var(--saffron)), hsl(var(--terracotta))) 1;
  }

  .paisley-pattern {
    background-image: radial-gradient(circle at 25% 25%, hsl(var(--saffron) / 0.1) 0%, transparent 50%),
                      radial-gradient(circle at 75% 75%, hsl(var(--terracotta) / 0.1) 0%, transparent 50%);
  }

  .heritage-gradient {
    background: linear-gradient(135deg, 
      hsl(var(--saffron)) 0%, 
      hsl(var(--marigold)) 25%, 
      hsl(var(--terracotta)) 50%, 
      hsl(var(--henna)) 75%, 
      hsl(var(--copper)) 100%);
  }

  .traditional-shadow {
    box-shadow: 0 4px 20px hsl(var(--henna) / 0.2), 
                0 8px 40px hsl(var(--terracotta) / 0.1);
  }

  .craft-border {
    border: 2px solid hsl(var(--terracotta));
    border-radius: 0.5rem;
    background: linear-gradient(45deg, 
      hsl(var(--warm-cream)), 
      hsl(var(--soft-beige)));
  }
}

/* Custom scrollbar styles */
.custom-scrollbar::-webkit-scrollbar {
  width: 8px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: #f1f5f9;
  border-radius: 4px;
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background: #cbd5e1;
  border-radius: 4px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background: #94a3b8;
}

/* For Firefox */
.custom-scrollbar {
  scrollbar-width: thin;
  scrollbar-color: #cbd5e1 #f1f5f9;
}

/* Hide scrollbar for Chrome, Safari and Opera */
.hide-scrollbar::-webkit-scrollbar {
  display: none;
}

/* Hide scrollbar for IE, Edge and Firefox */
.hide-scrollbar {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

/* Image zoom effect */
.image-zoom {
  transition: transform 0.3s ease;
}

.image-zoom:hover {
  transform: scale(1.05);
}

/* Smooth fade animation */
.fade-in {
  animation: fadeIn 0.5s ease-in;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Loading spinner */
.spinner {
  border: 2px solid #f3f3f3;
  border-top: 2px solid #3498db;
  border-radius: 50%;
  width: 20px;
  height: 20px;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Hover effects for buttons */
.btn-hover:hover {
  transform: translateY(-1px);
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
}

/* Card hover effects */
.card-hover:hover {
  transform: translateY(-2px);
  box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
}

/* Gradient backgrounds */
.gradient-bg {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
}

.gradient-text {
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Custom animations */
.slide-in {
  animation: slideIn 0.3s ease-out;
}

@keyframes slideIn {
  from {
    transform: translateX(-100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.bounce {
  animation: bounce 0.6s;
}

@keyframes bounce {
  0%, 20%, 53%, 80%, 100% {
    transform: translate3d(0,0,0);
  }
  40%, 43% {
    transform: translate3d(0,-15px,0);
  }
  70% {
    transform: translate3d(0,-7px,0);
  }
  90% {
    transform: translate3d(0,-2px,0);
  }
}

/* Apply the warm cream background to the body by default with subtle pattern */
body {
  background-color: hsl(var(--warm-cream));
  background-image: 
    radial-gradient(circle at 20% 50%, hsl(var(--saffron) / 0.03) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, hsl(var(--terracotta) / 0.03) 0%, transparent 50%),
    radial-gradient(circle at 40% 80%, hsl(var(--turmeric) / 0.02) 0%, transparent 50%);
  background-size: 400px 400px, 350px 350px, 300px 300px;
  background-position: 0 0, 40px 60px, 130px 270px;
}

/* Traditional Indian decorative patterns */
.ethnic-pattern {
  background-image: 
    repeating-linear-gradient(45deg, transparent, transparent 2px, hsl(var(--terracotta) / 0.05) 2px, hsl(var(--terracotta) / 0.05) 4px),
    repeating-linear-gradient(-45deg, transparent, transparent 2px, hsl(var(--saffron) / 0.05) 2px, hsl(var(--saffron) / 0.05) 4px);
}

.mandala-overlay {
  position: relative;
}

.mandala-overlay::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: 
    radial-gradient(circle at center, transparent 30%, hsl(var(--saffron) / 0.1) 31%, hsl(var(--saffron) / 0.1) 32%, transparent 33%),
    radial-gradient(circle at center, transparent 50%, hsl(var(--terracotta) / 0.05) 51%, hsl(var(--terracotta) / 0.05) 52%, transparent 53%);
  pointer-events: none;
}

/* Fullscreen modal styles */
[data-state="open"] .fullscreen-modal {
  position: fixed;
  inset: 0;
  z-index: 9999;
}

/* Hide scrollbar for fullscreen thumbnail navigation */
.fullscreen-thumbnails {
  scrollbar-width: none;
  -ms-overflow-style: none;
}

.fullscreen-thumbnails::-webkit-scrollbar {
  display: none;
}

/* Enhanced scrollbar hiding for horizontal scroll */
.scrollbar-hide {
  scrollbar-width: none;
  -ms-overflow-style: none;
}

.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

/* Custom animations */
@keyframes fade-in {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes fade-in-up {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slide-in-right {
  from {
    opacity: 0;
    transform: translateX(20px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes bounce-in {
  0% {
    opacity: 0;
    transform: scale(0.3);
  }
  50% {
    opacity: 1;
    transform: scale(1.05);
  }
  70% {
    opacity: 1;
    transform: scale(0.9);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

.animate-fade-in {
  animation: fade-in 0.6s ease-out;
}

.animate-fade-in-up {
  animation: fade-in-up 0.8s ease-out;
}

.animate-slide-in-right {
  animation: slide-in-right 0.6s ease-out;
}

.animate-bounce-in {
  animation: bounce-in 0.8s ease-out;
}

.animate-shimmer {
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
  background-size: 200% 100%;
  animation: shimmer 2s infinite;
}

/* Hover effects */
.hover-lift {
  transition: all 0.3s ease;
}

.hover-lift:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 25px rgba(0,0,0,0.1);
}

/* Glass morphism effect */
.glass {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

/* Gradient text */
.gradient-text {
  background: linear-gradient(135deg, #8B4513, #D2691E, #CD853F);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Custom border styles */
.border-3 {
  border-width: 3px;
}

/* Responsive fullscreen image container */
.fullscreen-image-container {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 100%;
  padding: 1rem;
}

@media (min-width: 640px) {
  .fullscreen-image-container {
    padding: 2rem;
  }
}

/* Prevent image overflow in fullscreen */
.fullscreen-image {
  max-width: 95vw;
  max-height: 80vh;
  width: auto;
  height: auto;
  object-fit: contain;
}

@media (min-width: 640px) {
  .fullscreen-image {
    max-width: 90vw;
    max-height: 85vh;
  }
}

/* Zoom lens styles */
.zoom-lens {
  border: 2px solid rgba(255, 255, 255, 0.8);
  box-shadow: 0 0 0 1px rgba(0, 0, 0, 0.2), 0 4px 12px rgba(0, 0, 0, 0.15);
  backdrop-filter: blur(1px);
}

/* Custom cursor for zoom area */
.cursor-zoom {
  cursor: zoom-in;
}

.cursor-zoom:hover {
  cursor: crosshair;
}

/* Smooth zoom transition */
.zoom-image {
  transition: transform 0.1s ease-out;
  transform-origin: center;
  will-change: transform;
}

/* Disable text selection during zoom */
.zoom-container * {
  user-select: none;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
}

/* Mobile-specific optimizations */
@media (max-width: 640px) {
  /* Ensure minimum touch target size */
  .touch-target {
    min-height: 44px;
    min-width: 44px;
  }

  /* Improve button spacing on mobile */
  .mobile-button-spacing {
    margin: 0.25rem;
    padding: 0.75rem 1rem;
  }

  /* Better text readability on mobile */
  .mobile-text {
    line-height: 1.6;
    font-size: 1rem;
  }

  /* Reduce margins for mobile */
  .mobile-spacing {
    margin: 0.5rem 0;
  }

  /* Hide elements that take up too much space on mobile */
  .desktop-only {
    display: none !important;
  }
}

/* Extra small screens */
@media (max-width: 375px) {
  /* Further reduce text sizes for very small screens */
  .text-xs { font-size: 0.7rem; }
  .text-sm { font-size: 0.8rem; }
  .text-base { font-size: 0.9rem; }

  /* Reduce padding */
  .px-4 { padding-left: 0.75rem; padding-right: 0.75rem; }
  .py-4 { padding-top: 0.75rem; padding-bottom: 0.75rem; }
}

/* Improve scroll performance on mobile */
.mobile-scroll {
  -webkit-overflow-scrolling: touch;
  scroll-behavior: smooth;
}

/* Better form inputs on mobile */
@media (max-width: 640px) {
  input[type="text"],
  input[type="email"],
  input[type="password"],
  textarea,
  select {
    font-size: 16px; /* Prevents zoom on iOS */
    padding: 0.75rem;
  }
}

