import { ShoppingCart, Heart } from "lucide-react";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useCart } from "@/hooks/use-cart";
import { useWishlist } from "@/hooks/use-wishlist";
import { useToast } from "@/hooks/use-toast";
import type { Product, ProductWithCategory } from "@shared/schema";
import { Star } from "lucide-react";

interface ProductCardProps {
  product: Product | ProductWithCategory;
  showCategory?: boolean;
  highlightFeatures?: string[];
}

export function ProductCard({ 
  product, 
  showCategory = true,
  highlightFeatures = [] 
}: ProductCardProps) {
  const { addToCart, isAddingToCart } = useCart();
  const { isInWishlist, toggleWishlist } = useWishlist();
  const { toast } = useToast();

  // Fetch review stats
  // const { data: reviewStats } = useQuery({
  //   queryKey: ["/api/products", product.id, "review-stats"],
  //   queryFn: () => getProductReviewStats(product.id),
  // });

  const handleAddToCart = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();

    if (product.stock <= 0) return;

    addToCart({
      productId: product.id,
      quantity: 1,
    });

    toast({
      title: "Added to cart",
      description: `${product.name} added to your cart.`,
    });
  };

  const handleWishlistToggle = (e: React.MouseEvent) => {
    e.preventDefault();
    e.stopPropagation();
    toggleWishlist(product.id);
  };

  const category = 'category' in product ? product.category : null;
  const productFeatures = product.features || [];
  const matchingFeatures = highlightFeatures.length > 0 
    ? productFeatures.filter(feature => 
        highlightFeatures.some(highlight => 
          feature.toLowerCase().includes(highlight.toLowerCase()) ||
          highlight.toLowerCase().includes(feature.toLowerCase())
        )
      ).slice(0, 2)
    : productFeatures.slice(0, 2);

  return (
    <Link href={`/products/${product.id}`}>
      <Card className="group cursor-pointer transition-all duration-300 traditional-shadow hover:scale-105 craft-border h-full flex flex-col bg-gradient-to-br from-warm-cream to-sandalwood/10 paisley-pattern">
        <div className="relative aspect-[4/3] sm:aspect-[4/5] overflow-hidden rounded-t-lg">
          {/* Product Image */}
          <img
            src={product.images[0] || "/placeholder-image.jpg"}
            alt={product.name}
            className="w-full h-full object-cover transition-transform duration-300 group-hover:scale-110"
          />

          {/* Traditional Badges */}
          <div className="absolute top-2 sm:top-3 left-2 sm:left-3 flex flex-col space-y-1 sm:space-y-2">
            {product.featured && (
              <Badge className="bg-saffron/90 text-white text-xs font-serif traditional-shadow">
                â­ Featured
              </Badge>
            )}
            {product.stock === 0 && (
              <Badge variant="destructive" className="text-xs font-serif">
                Out of Stock
              </Badge>
            )}
            {product.stock > 0 && product.stock <= 5 && (
              <Badge variant="outline" className="text-vermillion border-vermillion bg-warm-cream text-xs font-serif">
                Limited Stock
              </Badge>
            )}
          </div>

          {/* Traditional Add to Cart Button */}
          {product.stock > 0 && (
            <div className="absolute bottom-2 sm:bottom-3 right-2 sm:right-3 sm:opacity-0 sm:group-hover:opacity-100 transition-opacity duration-300">
              <Button
                size="sm"
                onClick={handleAddToCart}
                disabled={isAddingToCart}
                className="heritage-gradient hover:opacity-90 text-white traditional-shadow h-8 w-8 sm:h-9 sm:w-9 p-0 rounded-full"
              >
                <ShoppingCart className="h-3 w-3 sm:h-4 sm:w-4" />
              </Button>
            </div>
          )}
        </div>

        <CardContent className="p-2 sm:p-4 flex-1 flex flex-col bg-gradient-to-br from-warm-cream to-soft-beige/50">
          <div className="space-y-1 sm:space-y-2 flex-1">
            <div className="flex items-center justify-between">
              {showCategory && category && (
                <Badge variant="outline" className="text-xs hidden sm:block bg-terracotta/10 text-henna border-terracotta/30 font-serif">
                  {category.name}
                </Badge>
              )}
              <div className="flex items-center space-x-1">
                {[...Array(5)].map((_, i) => (
                  <Star key={i} className="h-2.5 w-2.5 sm:h-3 sm:w-3 fill-saffron text-saffron" />
                ))}
                <span className="text-xs text-copper ml-1 hidden sm:inline font-serif">4.8</span>
              </div>
            </div>

            <h3 className="font-serif font-semibold text-henna line-clamp-2 min-h-[1.5rem] sm:min-h-[2.5rem] text-xs sm:text-base leading-tight">
              {product.name}
            </h3>

            <p className="text-xs sm:text-sm text-copper/80 line-clamp-1 sm:line-clamp-2 min-h-[1rem] sm:min-h-[2.5rem] leading-tight font-serif">
              {product.description}
            </p>

            {/* Traditional Features */}
            {matchingFeatures.length > 0 && (
              <div className="space-y-1 hidden sm:block">
                <div className="text-xs text-henna font-serif">Features:</div>
                <div className="flex flex-wrap gap-1">
                  {matchingFeatures.map((feature, index) => (
                    <Badge 
                      key={index} 
                      variant="secondary" 
                      className={`text-xs font-serif ${
                        highlightFeatures.some(h => 
                          feature.toLowerCase().includes(h.toLowerCase()) ||
                          h.toLowerCase().includes(feature.toLowerCase())
                        ) ? 'bg-saffron/20 text-saffron border-saffron' : 'bg-sandalwood/20 text-henna border-terracotta/20'
                      }`}
                    >
                      {feature}
                    </Badge>
                  ))}
                </div>
              </div>
            )}
          </div>

          <div className="flex items-center justify-between pt-2 sm:pt-3 mt-auto bg-saffron/10 rounded-lg p-2 craft-border">
            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between w-full">
              <span className="text-sm sm:text-lg font-bold text-terracotta font-serif">
                â‚¹{parseFloat(product.price).toLocaleString()}
              </span>

              <div className="flex items-center justify-between sm:justify-end space-x-1 sm:space-x-2 mt-1 sm:mt-0">
                <div className="text-xs text-copper hidden sm:block font-serif">
                  {product.stock > 0 ? `${product.stock} available` : 'Out of stock'}
                </div>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => {
                    e.preventDefault();
                    e.stopPropagation();
                    toggleWishlist(product.id);
                  }}
                  className={`p-1 sm:p-2 transition-colors h-6 w-6 sm:h-8 sm:w-8 rounded-full traditional-shadow ${
                    isInWishlist(product.id) 
                      ? "bg-vermillion/20 text-vermillion hover:bg-vermillion/30" 
                      : "hover:bg-lotus/30 text-henna hover:text-vermillion"
                  }`}
                  aria-label={isInWishlist(product.id) ? "Remove from wishlist" : "Add to wishlist"}
                >
                  <Heart className={`h-2.5 w-2.5 sm:h-4 sm:w-4 ${isInWishlist(product.id) ? "fill-current" : ""}`} />
                </Button>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </Link>
  );
}

client/src/components/reviews.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Star, Edit, Trash2, MessageSquare } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar } from "@/components/ui/avatar";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Progress } from "@/components/ui/progress";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { getProductReviews, getProductReviewStats, createReview, deleteReview } from "@/lib/api";

interface ReviewsProps {
  productId: number;
}

interface ReviewFormData {
  rating: number;
  title: string;
  comment: string;
}

interface Review {
  id: number;
  userId: number;
  userName: string;
  rating: number;
  title: string;
  comment: string;
  createdAt: string;
}

interface ReviewStats {
  averageRating: number;
  totalReviews: number;
  ratingDistribution: { [key: number]: number };
}

export function Reviews({ productId }: ReviewsProps) {
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const [isReviewDialogOpen, setIsReviewDialogOpen] = useState(false);
  const [reviewForm, setReviewForm] = useState<ReviewFormData>({
    rating: 5,
    title: "",
    comment: "",
  });

  const queryClient = useQueryClient();

  const { data: reviews = [], isLoading: reviewsLoading } = useQuery({
    queryKey: ["/api/products", productId, "reviews"],
    queryFn: () => getProductReviews(productId),
  });

  const { data: reviewStats } = useQuery<ReviewStats>({
    queryKey: ["/api/products", productId, "review-stats"],
    queryFn: () => getProductReviewStats(productId),
  });

  const createReviewMutation = useMutation({
    mutationFn: (data: ReviewFormData) => createReview(productId, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products", productId, "reviews"] });
      queryClient.invalidateQueries({ queryKey: ["/api/products", productId, "review-stats"] });
      setIsReviewDialogOpen(false);
      setReviewForm({ rating: 5, title: "", comment: "" });
      toast({
        title: "Review Submitted",
        description: "Thank you for your review!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to submit review. Please try again.",
        variant: "destructive",
      });
    },
  });

  const deleteReviewMutation = useMutation({
    mutationFn: (reviewId: number) => deleteReview(reviewId),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products", productId, "reviews"] });
      queryClient.invalidateQueries({ queryKey: ["/api/products", productId, "review-stats"] });
      toast({
        title: "Review Deleted",
        description: "Your review has been deleted.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete review. Please try again.",
        variant: "destructive",
      });
    },
  });

  const renderStars = (rating: number, interactive = false, onRatingChange?: (rating: number) => void) => {
    return (
      <div className="flex space-x-0.5">
        {[...Array(5)].map((_, i) => (
          <button
            key={i}
            type={interactive ? "button" : undefined}
            onClick={interactive ? () => onRatingChange?.(i + 1) : undefined}
            className={interactive ? "cursor-pointer focus:outline-none" : undefined}
          >
            <Star className={`h-4 w-4 ${
              i < rating ? "fill-yellow-400 text-yellow-400" : "text-gray-300"
            }`} />
          </button>
        ))}
      </div>
    );
  };

  const handleSubmitReview = (e: React.FormEvent) => {
    e.preventDefault();
    if (!reviewForm.title.trim() || !reviewForm.comment.trim()) {
      toast({
        title: "Error",
        description: "Please fill in all fields",
        variant: "destructive",
      });
      return;
    }
    createReviewMutation.mutate(reviewForm);
  };

  const userHasReviewed = user && reviews.some((review: Review) => review.userId === user.id);

  return (
    <div className="space-y-8">
      {/* Review Stats */}
      {reviewStats && reviewStats.totalReviews > 0 && (
        <Card>
          <CardHeader className="pb-3 sm:pb-6">
            <h3 className="font-semibold text-base sm:text-lg">Customer Reviews</h3>
          </CardHeader>
          <CardContent className="pt-0">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3 sm:gap-6">
              {/* Overall Rating */}
              <div className="text-center">
                <div className="text-2xl sm:text-4xl font-bold text-gray-900 mb-1 sm:mb-2">
                  {reviewStats.averageRating.toFixed(1)}
                </div>
                <div className="flex justify-center mb-1 sm:mb-2">
                  <div className="flex space-x-0.5">
                    {renderStars(Math.round(reviewStats.averageRating))}
                  </div>
                </div>
                <p className="text-gray-600 text-xs sm:text-sm">
                  Based on {reviewStats.totalReviews} {reviewStats.totalReviews === 1 ? 'review' : 'reviews'}
                </p>
              </div>

              {/* Rating Distribution */}
              <div className="space-y-1 sm:space-y-2">
                {[5, 4, 3, 2, 1].map((rating) => (
                  <div key={rating} className="flex items-center space-x-2 sm:space-x-3">
                    <div className="flex items-center space-x-1 w-10 sm:w-16">
                      <span className="text-xs sm:text-sm">{rating}</span>
                      <Star className="h-3 w-3 sm:h-4 sm:w-4 fill-yellow-400 text-yellow-400" />
                    </div>
                    <Progress
                      value={(reviewStats.ratingDistribution[rating] / reviewStats.totalReviews) * 100}
                      className="flex-1 h-1.5 sm:h-2"
                    />
                    <span className="text-xs sm:text-sm text-gray-600 w-4 sm:w-8">
                      {reviewStats.ratingDistribution[rating]}
                    </span>
                  </div>
                ))}
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Write Review Button */}
      {isAuthenticated && !userHasReviewed && (
        <div className="text-center">
          <Button
            onClick={() => setIsReviewDialogOpen(true)}
            className="bg-terracotta hover:bg-terracotta/90 text-sm sm:text-base px-3 sm:px-4 py-2"
            size="sm"
          >
            <MessageSquare className="h-3 w-3 sm:h-4 sm:w-4 mr-1 sm:mr-2" />
            <span className="hidden sm:inline">Write a Review</span>
            <span className="sm:hidden">Review</span>
          </Button>
        </div>
      )}

      {/* Reviews List */}
      <div className="space-y-3 sm:space-y-4">
        <h3 className="font-semibold text-base sm:text-lg">
          Reviews ({reviews.length})
        </h3>

        {reviewsLoading ? (
          <div className="space-y-4">
            {[...Array(3)].map((_, i) => (
              <Card key={i} className="animate-pulse">
                <CardContent className="pt-6">
                  <div className="space-y-3">
                    <div className="h-4 bg-gray-200 rounded w-1/4"></div>
                    <div className="h-4 bg-gray-200 rounded w-1/2"></div>
                    <div className="h-20 bg-gray-200 rounded"></div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : reviews.length === 0 ? (
          <Card>
            <CardContent className="pt-6 text-center py-12">
              <MessageSquare className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-600">No reviews yet. Be the first to review this product!</p>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-2 sm:space-y-4">
            {reviews.map((review: Review) => (
              <Card key={review.id}>
                <CardContent className="pt-3 sm:pt-6 pb-3 sm:pb-6">
                  <div className="flex items-start justify-between mb-2 sm:mb-4">
                    <div className="flex items-center space-x-2 sm:space-x-3">
                      <Avatar className="h-8 w-8 sm:h-10 sm:w-10 bg-terracotta text-white">
                        <span className="text-xs sm:text-sm font-semibold">
                          {review.userName.charAt(0).toUpperCase()}
                        </span>
                      </Avatar>
                      <div>
                        <p className="font-medium text-sm sm:text-base">{review.userName}</p>
                        <div className="flex items-center space-x-1 sm:space-x-2">
                          <div className="flex space-x-0.5">
                            {renderStars(review.rating)}
                          </div>
                          <span className="text-xs sm:text-sm text-gray-500">
                            {new Date(review.createdAt).toLocaleDateString()}
                          </span>
                        </div>
                      </div>
                    </div>
                    {user && user.id === review.userId && (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => deleteReviewMutation.mutate(review.id)}
                        className="text-red-600 hover:text-red-700 h-6 w-6 sm:h-8 sm:w-8 p-0"
                      >
                        <Trash2 className="h-3 w-3 sm:h-4 sm:w-4" />
                      </Button>
                    )}
                  </div>
                  <h4 className="font-semibold mb-1 sm:mb-2 text-sm sm:text-base">{review.title}</h4>
                  <p className="text-gray-700 leading-relaxed text-sm sm:text-base">{review.comment}</p>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>

      {/* Review Dialog */}
      <Dialog open={isReviewDialogOpen} onOpenChange={setIsReviewDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Write a Review</DialogTitle>
          </DialogHeader>
          <form onSubmit={handleSubmitReview} className="space-y-4">
            <div>
              <label className="block text-sm font-medium mb-2">Rating</label>
              {renderStars(reviewForm.rating, true, (rating) =>
                setReviewForm({ ...reviewForm, rating })
              )}
            </div>
            <div>
              <label className="block text-sm font-medium mb-2">Title</label>
              <Input
                value={reviewForm.title}
                onChange={(e) => setReviewForm({ ...reviewForm, title: e.target.value })}
                placeholder="Sum up your review in a few words"
                maxLength={100}
              />
            </div>
            <div>
              <label className="block text-sm font-medium mb-2">Review</label>
              <Textarea
                value={reviewForm.comment}
                onChange={(e) => setReviewForm({ ...reviewForm, comment: e.target.value })}
                placeholder="Share your thoughts about this product"
                rows={4}
                maxLength={1000}
              />
              <p className="text-xs text-gray-500 mt-1">
                {reviewForm.comment.length}/1000 characters
              </p>
            </div>
            <div className="flex justify-end space-x-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsReviewDialogOpen(false)}
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createReviewMutation.isPending}
                className="bg-terracotta hover:bg-terracotta/90"
              >
                {createReviewMutation.isPending ? "Submitting..." : "Submit Review"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>
    </div>
  );
}

