import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Filter, Search, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetTrigger } from "@/components/ui/sheet";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { ProductCard } from "@/components/product-card";
import { getProducts, getCategories } from "@/lib/api";

export default function Products() {
  const [location] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [sortBy, setSortBy] = useState("name");

  // Parse URL parameters
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const category = params.get("category") || "";
    const search = params.get("search") || "";

    setSelectedCategory(category);
    setSearchQuery(search);
  }, [location]);

  const { data: categories = [] } = useQuery({
    queryKey: ["/api/categories"],
    queryFn: getCategories,
  });

  const { data: products = [], isLoading } = useQuery({
    queryKey: ["/api/products", { category: selectedCategory, search: searchQuery }],
    queryFn: () => getProducts({ 
      category: selectedCategory || undefined, 
      search: searchQuery || undefined 
    }),
  });

  // Sort products
  const sortedProducts = [...products].sort((a, b) => {
    switch (sortBy) {
      case "price-low":
        return parseFloat(a.price) - parseFloat(b.price);
      case "price-high":
        return parseFloat(b.price) - parseFloat(a.price);
      case "name":
      default:
        return a.name.localeCompare(b.name);
    }
  });

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    updateURL();
  };

  const updateURL = () => {
    const params = new URLSearchParams();
    if (selectedCategory) params.set("category", selectedCategory);
    if (searchQuery) params.set("search", searchQuery);

    const newURL = `/products${params.toString() ? `?${params}` : ""}`;
    window.history.pushState({}, "", newURL);
  };

  const clearFilters = () => {
    setSelectedCategory("");
    setSearchQuery("");
    window.history.pushState({}, "", "/products");
  };

  const hasFilters = selectedCategory || searchQuery;

  return (
    <div className="min-h-screen bg-warm-cream">
      <div className="max-w-7xl mx-auto px-3 sm:px-6 lg:px-8 py-4 sm:py-8">
        {/* Header */}
        <div className="mb-4 sm:mb-8">
          <h1 className="font-display text-xl sm:text-3xl font-bold text-gray-900 mb-2 sm:mb-4">
            Premium Makhana Collection
          </h1>
          <p className="text-sm sm:text-base text-gray-600">
            Discover authentic makhana varieties processed by skilled farmers across India
          </p>
        </div>

        {/* Filters and Search */}
        <div className="mb-4 sm:mb-8">
          {/* Mobile Layout */}
          <div className="block sm:hidden space-y-3">
            {/* Search - Full width on mobile */}
            <form onSubmit={handleSearch} className="relative">
              <Input
                type="text"
                placeholder="Search makhana..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10"
              />
              <Search className="h-4 w-4 text-gray-500 absolute left-3 top-1/2 -translate-y-1/2" />
            </form>

            {/* Filters Row - Category and Sort side by side */}
            <div className="grid grid-cols-2 gap-2">
              <Select
                value={selectedCategory || "all"}
                onValueChange={(value) => {
                  setSelectedCategory(value === "all" ? "" : value);
                  setTimeout(updateURL, 0);
                }}
              >
                <SelectTrigger className="text-xs">
                  <SelectValue placeholder="All Categories" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  {categories.map((category) => (
                    <SelectItem key={category.id} value={category.slug}>
                      {category.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={sortBy} onValueChange={setSortBy}>
                <SelectTrigger className="text-xs">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="name">Name A-Z</SelectItem>
                  <SelectItem value="price-low">Price: Low to High</SelectItem>
                  <SelectItem value="price-high">Price: High to Low</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Desktop Layout */}
          <div className="hidden sm:flex flex-col lg:flex-row gap-4 items-start lg:items-center justify-between">
            <div className="flex flex-col sm:flex-row gap-4 flex-1">
              {/* Search */}
              <form onSubmit={handleSearch} className="relative flex-1 max-w-md">
                <Input
                  type="text"
                  placeholder="Search makhana..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pr-10"
                />
                <Button
                  type="submit"
                  size="sm"
                  variant="ghost"
                  className="absolute right-0 top-0 h-full px-3"
                >
                  <Search className="h-4 w-4" />
                </Button>
              </form>

              {/* Category Filter */}
              <Select
                value={selectedCategory || "all"}
                onValueChange={(value) => {
                  setSelectedCategory(value === "all" ? "" : value);
                  setTimeout(updateURL, 0);
                }}
              >
                <SelectTrigger className="w-48">
                  <SelectValue placeholder="All Categories" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Categories</SelectItem>
                  {categories.map((category) => (
                    <SelectItem key={category.id} value={category.slug}>
                      {category.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Sort */}
            <div className="flex items-center gap-4">
              <Select value={sortBy} onValueChange={setSortBy}>
                <SelectTrigger className="w-40">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="name">Name A-Z</SelectItem>
                  <SelectItem value="price-low">Price: Low to High</SelectItem>
                  <SelectItem value="price-high">Price: High to Low</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Active Filters */}
          {hasFilters && (
            <div className="flex flex-wrap items-center gap-1 sm:gap-2 mt-3 sm:mt-4">
              <span className="text-xs sm:text-sm text-gray-600">Filters:</span>
              {selectedCategory && (
                <Badge variant="secondary" className="flex items-center gap-1 text-xs">
                  {categories.find(c => c.slug === selectedCategory)?.name}
                  <X 
                    className="h-3 w-3 cursor-pointer" 
                    onClick={() => {
                      setSelectedCategory("");
                      setTimeout(updateURL, 0);
                    }}
                  />
                </Badge>
              )}
              {searchQuery && (
                <Badge variant="secondary" className="flex items-center gap-1 text-xs">
                  "{searchQuery}"
                  <X 
                    className="h-3 w-3 cursor-pointer" 
                    onClick={() => {
                      setSearchQuery("");
                      setTimeout(updateURL, 0);
                    }}
                  />
                </Badge>
              )}
              <Button
                variant="ghost"
                size="sm"
                onClick={clearFilters}
                className="text-terracotta hover:text-terracotta/80 text-xs px-2 py-1 h-auto"
              >
                Clear
              </Button>
            </div>
          )}
        </div>

        {/* Products Grid */}
        <div className="mb-3 sm:mb-6">
          <p className="text-xs sm:text-base text-gray-600">
            {isLoading ? "Loading..." : `${sortedProducts.length} products found`}
          </p>
        </div>

        {isLoading ? (
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2 sm:gap-4 lg:gap-6">
            {[...Array(8)].map((_, i) => (
              <div key={i} className="space-y-2">
                <Skeleton className="h-32 sm:h-40 lg:h-48 w-full" />
                <Skeleton className="h-3 sm:h-4 w-3/4" />
                <Skeleton className="h-3 sm:h-4 w-1/2" />
                <Skeleton className="h-6 sm:h-8 w-full" />
              </div>
            ))}
          </div>
        ) : sortedProducts.length === 0 ? (
          <div className="text-center py-8 sm:py-12">
            <div className="text-gray-400 mb-4">
              <svg className="w-12 h-12 sm:w-16 sm:h-16 mx-auto" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M20 13V6a2 2 0 00-2-2H6a2 2 0 00-2 2v7m16 0v5a2 2 0 01-2 2H6a2 2 0 01-2-2v-5m16 0h-2M4 13h2m13-8l-4 4m0 0l-4-4m4 4V3" />
              </svg>
            </div>
            <h3 className="text-base sm:text-lg font-medium text-gray-900 mb-2">No products found</h3>
            <p className="text-sm sm:text-base text-gray-600 mb-4 px-4">
              Try adjusting your search criteria or browse all products.
            </p>
            <Button onClick={clearFilters} className="bg-terracotta hover:bg-terracotta/90 text-sm sm:text-base">
              View All Products
            </Button>
          </div>
        ) : (
          <>
            <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2 sm:gap-4 lg:gap-6">
              {sortedProducts.map((product) => (
                <ProductCard key={product.id} product={product} showCategory />
              ))}
            </div>

            {/* Recommended Products Section - Compact for Mobile */}
            {!selectedCategory && !searchQuery && (
              <div className="mt-6 sm:mt-12 border-t pt-4 sm:pt-8">
                <div className="mb-3 sm:mb-6">
                  <h2 className="font-display text-base sm:text-xl font-bold text-gray-900 mb-1">
                    You might also like
                  </h2>
                  <p className="text-xs sm:text-base text-gray-600 hidden sm:block">
                    Discover more premium makhana varieties from our farmers
                  </p>
                </div>

                {/* Mobile: Horizontal scroll, Desktop: Grid */}
                <div className="sm:hidden">
                  <div className="flex space-x-2 overflow-x-auto pb-2 scrollbar-hide">
                    {products
                      .filter(product => product.featured)
                      .slice(0, 6)
                      .map((product) => (
                        <div key={`featured-mobile-${product.id}`} className="flex-shrink-0 w-36">
                          <ProductCard product={product} showCategory />
                        </div>
                      ))}
                  </div>
                </div>

                {/* Desktop: Normal Grid */}
                <div className="hidden sm:grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 lg:gap-6">
                  {products
                    .filter(product => product.featured)
                    .slice(0, 4)
                    .map((product) => (
                      <ProductCard key={`featured-desktop-${product.id}`} product={product} showCategory />
                    ))}
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

client/src/pages/track-order.tsx.content:
import React, { useState } from "react";
import { Search, Package, Truck, CheckCircle, XCircle, Clock } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { trackOrder } from "@/lib/api";

const statusIcons = {
  pending: <Clock className="h-5 w-5 text-yellow-600" />,
  confirmed: <Package className="h-5 w-5 text-blue-600" />,
  shipped: <Truck className="h-5 w-5 text-purple-600" />,
  delivered: <CheckCircle className="h-5 w-5 text-green-600" />,
  cancelled: <XCircle className="h-5 w-5 text-red-600" />,
};

const statusColors = {
  pending: "bg-yellow-100 text-yellow-800",
  confirmed: "bg-blue-100 text-blue-800",
  shipped: "bg-purple-100 text-purple-800",
  delivered: "bg-green-100 text-green-800",
  cancelled: "bg-red-100 text-red-800",
};

export default function TrackOrder() {
  const [trackingNumber, setTrackingNumber] = useState("");
  const [orderInfo, setOrderInfo] = useState<any>(null);
  const [isTracking, setIsTracking] = useState(false);
  const [error, setError] = useState("");
  const { toast } = useToast();

  const handleTrack = async () => {
    if (!trackingNumber.trim()) {
      setError("Please enter a tracking number");
      return;
    }

    setIsTracking(true);
    setError("");
    setOrderInfo(null);

    try {
      const result = await trackOrder(trackingNumber.trim());
      setOrderInfo(result);
    } catch (err: any) {
      setError(err.message || "Order not found");
    } finally {
      setIsTracking(false);
    }
  };

  const getStatusSteps = (currentStatus: string) => {
    const steps = [
      { key: "pending", label: "Order Placed", description: "Your order has been received" },
      { key: "confirmed", label: "Confirmed", description: "Order confirmed and being prepared" },
      { key: "shipped", label: "Shipped", description: "Order is on its way" },
      { key: "delivered", label: "Delivered", description: "Order has been delivered" },
    ];

    const statusOrder = ["pending", "confirmed", "shipped", "delivered"];
    const currentIndex = statusOrder.indexOf(currentStatus);

    return steps.map((step, index) => ({
      ...step,
      completed: index <= currentIndex,
      active: index === currentIndex,
    }));
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Track Your Order</h1>
          <p className="text-lg text-gray-600">
            Enter your tracking number to see the current status of your order
          </p>
        </div>

        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Enter Tracking Number</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="tracking">Tracking Number</Label>
              <div className="flex space-x-2 mt-1">
                <Input
                  id="tracking"
                  value={trackingNumber}
                  onChange={(e) => setTrackingNumber(e.target.value.toUpperCase())}
                  placeholder="Enter your tracking number (e.g., TRKXXXXXXXX)"
                  className="font-mono"
                  onKeyPress={(e) => e.key === 'Enter' && handleTrack()}
                />
                <Button onClick={handleTrack} disabled={isTracking}>
                  <Search className="h-4 w-4 mr-2" />
                  {isTracking ? "Tracking..." : "Track"}
                </Button>
              </div>
              {error && (
                <p className="text-sm text-red-600 mt-2">{error}</p>
              )}
            </div>
          </CardContent>
        </Card>

        {orderInfo && (
          <div className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center justify-between">
                  <span>Order Details</span>
                  <Badge className={statusColors[orderInfo.status as keyof typeof statusColors]}>
                    {statusIcons[orderInfo.status as keyof typeof statusIcons]}
                    <span className="ml-2 capitalize">{orderInfo.status}</span>
                  </Badge>
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label className="text-sm font-medium text-gray-500">Order ID</Label>
                    <p className="text-lg font-medium">#{orderInfo.id}</p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium text-gray-500">Tracking Number</Label>
                    <p className="text-lg font-mono">{orderInfo.trackingNumber}</p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium text-gray-500">Customer</Label>
                    <p className="text-lg">{orderInfo.customerName}</p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium text-gray-500">Order Total</Label>
                    <p className="text-lg font-medium">â‚¹{parseFloat(orderInfo.total).toLocaleString()}</p>
                  </div>
                  <div>
                    <Label className="text-sm font-medium text-gray-500">Order Date</Label>
                    <p className="text-lg">{new Date(orderInfo.createdAt).toLocaleDateString()}</p>
                  </div>
                </div>
              </CardContent>
            </Card>

            {orderInfo.status !== "cancelled" && (
              <Card>
                <CardHeader>
                  <CardTitle>Order Progress</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-6">
                    {getStatusSteps(orderInfo.status).map((step, index) => (
                      <div key={step.key} className="flex items-start">
                        <div className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center ${
                          step.completed 
                            ? 'bg-terracotta text-white' 
                            : step.active 
                              ? 'bg-terracotta/20 text-terracotta border-2 border-terracotta'
                              : 'bg-gray-200 text-gray-400'
                        }`}>
                          {step.completed ? (
                            <CheckCircle className="h-5 w-5" />
                          ) : (
                            <span className="text-sm font-medium">{index + 1}</span>
                          )}
                        </div>
                        <div className="ml-4 flex-1">
                          <h3 className={`text-lg font-medium ${
                            step.completed || step.active ? 'text-gray-900' : 'text-gray-400'
                          }`}>
                            {step.label}
                          </h3>
                          <p className={`text-sm ${
                            step.completed || step.active ? 'text-gray-600' : 'text-gray-400'
                          }`}>
                            {step.description}
                          </p>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

client/src/pages/wishlist.tsx.content:
import { Heart, ArrowLeft, Trash2, ShoppingCart, Plus } from "lucide-react";
import { Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useWishlist } from "@/hooks/use-wishlist";
import { useCart } from "@/hooks/use-cart";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";

export default function Wishlist() {
  const { isAuthenticated } = useAuth();
  const { wishlistItems, isLoading, toggleWishlist } = useWishlist();
  const { addToCart, isAddingToCart } = useCart();
  const { toast } = useToast();

  const addToCartFromWishlist = (item: any) => {
    addToCart({
      productId: item.id,
      quantity: 1,
    });

    toast({
      title: "Added to Cart",
      description: `${item.name} has been added to your cart.`,
    });
  };

  const clearWishlist = () => {
    // Remove all items from wishlist
    wishlistItems.forEach(item => {
      toggleWishlist(item.id);
    });

    toast({
      title: "Wishlist Cleared",
      description: "All items have been removed from your wishlist.",
    });
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-warm-cream">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
          <div className="text-center">
            <h1 className="font-display text-3xl font-bold text-gray-900 mb-4">
              Please Login
            </h1>
            <p className="text-gray-600 mb-8">
              You need to be logged in to view your wishlist.
            </p>
            <Link href="/">
              <Button size="lg" className="bg-terracotta hover:bg-terracotta/90">
                Go to Home
              </Button>
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-warm-cream flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-terracotta mx-auto mb-4"></div>
          <p className="text-gray-600">Loading wishlist...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-warm-cream">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex items-center justify-between mb-8">
          <div className="flex items-center space-x-3">
            <Heart className="h-8 w-8 text-terracotta" />
            <h1 className="text-3xl font-bold text-gray-900">My Wishlist</h1>
            {wishlistItems.length > 0 && (
              <Badge className="bg-terracotta text-white">
                {wishlistItems.length} items
              </Badge>
            )}
          </div>

          <div className="flex items-center space-x-3">
            <Link href="/products">
              <Button variant="outline" className="border-gray-300 text-gray-700 hover:bg-gray-50">
                <ArrowLeft className="h-4 w-4 mr-2" />
                Continue Shopping
              </Button>
            </Link>
            {wishlistItems.length > 0 && (
              <Button 
                variant="outline" 
                onClick={clearWishlist}
                className="border-red-300 text-red-600 hover:bg-red-50"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Clear All
              </Button>
            )}
          </div>
        </div>

        {wishlistItems.length === 0 ? (
          <div className="text-center py-16">
            <Heart className="h-24 w-24 text-gray-300 mx-auto mb-6" />
            <h2 className="text-2xl font-semibold text-gray-900 mb-4">Your wishlist is empty</h2>
            <p className="text-gray-600 mb-8 max-w-md mx-auto">
              Start browsing our collection and add items to your wishlist to save them for later.
            </p>
            <Link href="/products">
              <Button className="bg-terracotta hover:bg-terracotta/90 text-white">
                <ShoppingCart className="h-5 w-5 mr-2" />
                Start Shopping
              </Button>
            </Link>
          </div>
        ) : (
          <div className="grid grid-cols-2 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3 sm:gap-6">
            {wishlistItems.map((item) => (
              <Card key={item.id} className="overflow-hidden hover:shadow-lg transition-shadow duration-300">
                <div className="relative">
                  <img
                    src={item.images?.[0] || '/placeholder-image.jpg'}
                    alt={item.name}
                    className="w-full h-32 sm:h-48 object-cover"
                  />
                  <button
                    onClick={() => toggleWishlist(item.id)}
                    className="absolute top-2 right-2 w-8 h-8 bg-white/80 hover:bg-white text-red-500 rounded-full flex items-center justify-center transition-colors duration-200"
                  >
                    <Trash2 className="h-4 w-4" />
                  </button>
                </div>
                <CardContent className="p-3 sm:p-4">
                  <h3 className="font-semibold text-gray-900 mb-2 line-clamp-2 text-sm sm:text-base">{item.name}</h3>
                  <p className="text-gray-600 text-xs sm:text-sm mb-3 line-clamp-2">{item.description}</p>
                  <div className="flex items-center justify-between mb-3 sm:mb-4">
                    <span className="text-lg sm:text-2xl font-bold text-terracotta">
                      â‚¹{parseFloat(item.price).toLocaleString()}
                    </span>
                  </div>
                  <div className="flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-2">
                    <Link href={`/products/${item.id}`} className="flex-1">
                      <Button variant="outline" className="w-full border-terracotta text-terracotta hover:bg-terracotta hover:text-white text-xs sm:text-sm">
                        View Details
                      </Button>
                    </Link>
                    <Button
                      onClick={() => addToCartFromWishlist(item)}
                      disabled={isAddingToCart}
                      className="flex-1 bg-terracotta hover:bg-terracotta/90 text-white text-xs sm:text-sm"
                    >
                      <Plus className="h-3 w-3 sm:h-4 sm:w-4 mr-1" />
                      Add to Cart
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

